    public static ArrayList<Wisata> getListData(){
        ArrayList<Wisata> list = new ArrayList<>();
        for (String[] aData : data){
            Wisata wisata = new Wisata();
            wisata.setTempat(aData[0]);
            wisata.setAlamat(aData[1]);
            wisata.setTelepon(aData[2]);
            wisata.setHarga(aData[3]);
            wisata.setJam(aData[4]);
            wisata.setSubs(aData[5]);
            wisata.setPhoto(aData[6]);
            wisata.setRating(aData[7]);

            list.add(wisata);
        }
        return list;
    }
